pragma solidity 0.4.23;

import "../zeppelin-solidity/contracts/ownership/Ownable.sol";
import "./ORSToken.sol";


/// @title ORSAirdrop
/// @author Autogenerated from a Dia UML diagram
contract ORSAirdrop is Ownable {

    ORSToken public token;
    address public initialTokenOwner;

    /// @dev Constructor
    /// @param _token An Ethereum address
    constructor(address _token) public {
        token = ORSToken(_token);
        initialTokenOwner = token.owner();
    }

    /// @dev Distribute
    /// @param _recipients A list where each entry is an Ethereum address
    /// @param _amounts A list where each entry is a positive number
    function distribute(address[] _recipients, uint[] _amounts) public onlyOwner {
        require(token.owner() == address(this));
        require(_recipients.length == _amounts.length);

        for (uint i = 0; i < _recipients.length; ++i) {
            token.mint(_recipients[i], _amounts[i]);
        }
    }

    /// @dev Finish distribution
    function finishDistribution() public onlyOwner {
        token.transferOwnership(initialTokenOwner);
    }

}

